type DocxFont struct {
	EastAsia string
	HAnsi    string
	Ascii    string
}
type DocxParagraph struct {
	Text string
	Font DocxFont
	Size int
	Type int
}

func NewDocPragraph(text string) *DocxParagraph {
	return &DocxParagraph{
		Text: text,
		Font: DocxFont{
			EastAsia: "方正仿宋简体",
			HAnsi:    "Times New Roman",
			Ascii:    "Times New Roman",
		},
		Size: 32,
	}
}

func (p *DocxParagraph) AsNormal() *DocxParagraph {
	p.Font.EastAsia = "方正仿宋简体"
	p.Size = 32
	p.Type = TYPE_NORMAL
	return p

}

func (p *DocxParagraph) AsTitle() *DocxParagraph {
	p.Font.EastAsia = "方正小标宋简体"
	p.Size = 44
	p.Type = TYPE_TITLE
	return p
}
func (p *DocxParagraph) AsH1() *DocxParagraph {
	p.Font.EastAsia = "黑体"
	p.Size = 36
	p.Type = TYPE_H1
	return p
}
func (p *DocxParagraph) AsH2() *DocxParagraph {
	p.Font.EastAsia = "楷体_GB2312"
	p.Size = 36
	p.Type = TYPE_H2
	return p
}

type DocxCreator struct {
	Paragraphs []*DocxParagraph
}

func (dc *DocxCreator) ExportDocx(docxfilepath string) error {
	st, err := os.Stat(docxfilepath)
	if err == nil && !st.IsDir() {
		os.Remove(docxfilepath)
	}
	tmpDir := os.TempDir()
	base := time.Now().String()
	baseDir := filepath.Join(tmpDir, base)
	os.Mkdir(baseDir, os.ModePerm)

	wordDir := filepath.Join(baseDir, "word")
	os.Mkdir(wordDir, os.ModePerm)
	word_rels := filepath.Join(wordDir, "_rels")
	os.Mkdir(word_rels, os.ModePerm)
	word_media := filepath.Join(wordDir, "media")
	os.Mkdir(word_media, os.ModePerm)
	word_theme := filepath.Join(wordDir, "theme")
	os.Mkdir(word_theme, os.ModePerm)

	docProps := filepath.Join(baseDir, "docProps")
	os.Mkdir(docProps, os.ModePerm)
	customXml := filepath.Join(baseDir, "customXml")
	os.Mkdir(customXml, os.ModePerm)
	_rels := filepath.Join(baseDir, "_rels")
	os.Mkdir(_rels, os.ModePerm)
	customXml_rels := filepath.Join(customXml, "_rels")
	os.Mkdir(customXml_rels, os.ModePerm)

	_rels_rels := filepath.Join(_rels, ".rels")
	os.WriteFile(_rels_rels, []byte(_rels_rels_content), os.ModePerm)
	customXml_rels_item1 := filepath.Join(customXml_rels, "item1.xml.rels")
	os.WriteFile(customXml_rels_item1, []byte(_rels_item1_content), os.ModePerm)
	customXml_item1 := filepath.Join(customXml, "item1.xml")
	os.WriteFile(customXml_item1, []byte(customXml_item1_content), os.ModePerm)
	customXml_itemProps1 := filepath.Join(customXml, "itemProps1.xml")
	os.WriteFile(customXml_itemProps1, []byte(customXml_itemProps1_content), os.ModePerm)

	docProps_app := filepath.Join(docProps, "app.xml")
	os.WriteFile(docProps_app, []byte(docProps_app_content), os.ModePerm)
	docProps_core := filepath.Join(docProps, "core.xml")
	os.WriteFile(docProps_core, []byte(docProps_core_content), os.ModePerm)
	docProps_custom := filepath.Join(docProps, "custom.xml")
	os.WriteFile(docProps_custom, []byte(docProps_custom_content), os.ModePerm)

	word_rels_document_rels := filepath.Join(word_rels, "document.xml.rels")
	os.WriteFile(word_rels_document_rels, []byte(word_rels_document_rels_content), os.ModePerm)
	word_theme_theme := filepath.Join(word_theme, "theme1.xml")
	os.WriteFile(word_theme_theme, []byte(word_theme_theme_content), os.ModePerm)
	word_fontTable := filepath.Join(wordDir, "fontTable.xml")
	os.WriteFile(word_fontTable, []byte(word_fontTable_content), os.ModePerm)
	word_numbering := filepath.Join(wordDir, "numbering.xml")
	os.WriteFile(word_numbering, []byte(word_numbering_content), os.ModePerm)
	word_settings := filepath.Join(wordDir, "settings.xml")
	os.WriteFile(word_settings, []byte(word_settings_content), os.ModePerm)
	word_styles := filepath.Join(wordDir, "styles.xml")
	os.WriteFile(word_styles, []byte(word_styles_content), os.ModePerm)
	word_styleWithEffects := filepath.Join(wordDir, "styleWithEffects.xml")
	os.WriteFile(word_styleWithEffects, []byte(word_styleWithEffects_content), os.ModePerm)
	word_webSettings := filepath.Join(wordDir, "webSettings.xml")
	os.WriteFile(word_webSettings, []byte(word_webSeetings_content), os.ModePerm)

	Content_Types := filepath.Join(baseDir, "[Content_Types].xml")
	os.WriteFile(Content_Types, []byte(Content_Types_content), os.ModePerm)

	documentXmlContent := dc.Xml()
	documentXml := filepath.Join(wordDir, "document.xml")
	os.WriteFile(documentXml, []byte(documentXmlContent), os.ModePerm)

	// 创建zip文件
	docxfilepathZip := docxfilepath + ".zip"
	zipFile, err := os.Create(docxfilepathZip)
	if err != nil {

		return err
	}
	defer zipFile.Close()
	// 创建zip.Writer
	zipWriter := zip.NewWriter(zipFile)
	defer zipWriter.Close()
	// 将文件写入zip文件
	files := []string{
		documentXml,
		Content_Types,
		word_webSettings,
		word_styleWithEffects,
		word_styles,
		word_settings,
		word_numbering,
		word_fontTable,
		word_theme_theme,
		word_rels_document_rels,
		docProps_custom,
		docProps_core,
		docProps_app,
		customXml_itemProps1,
		customXml_item1,
		customXml_rels_item1,
		_rels_rels,
	}
	for _, file := range files {

		f, err := os.Open(file)
		if err != nil {
			return err
		}
		defer f.Close()
		// 创建zip文件中的文件
		zname := strings.ReplaceAll(file, baseDir, "")
		zname = strings.TrimPrefix(zname, "/")
		w, err := zipWriter.Create(zname)
		if err != nil {
			return err
		}
		// 将文件内容写入zip文件
		_, err = io.Copy(w, f)
		if err != nil {
			return err
		}
	}
	os.Rename(docxfilepathZip, docxfilepath)

	return nil

}

func (dc *DocxCreator) LastPragraph() *DocxParagraph {
	return dc.Paragraphs[len(dc.Paragraphs)-1]
}

func (dc *DocxCreator) AddParagraph(text string) {
	dc.Paragraphs = append(dc.Paragraphs, NewDocPragraph(text))
}

func (dc *DocxCreator) AddSepParagraph() {
	p := NewDocPragraph("")
	p.Type = TYPE_SEP
	dc.Paragraphs = append(dc.Paragraphs, p)
}

func NewDocCreator() *DocxCreator {
	return &DocxCreator{
		Paragraphs: []*DocxParagraph{},
	}
}
